import React, { Component } from 'react';

class Register extends Component {
    mediaQuery = "(max-width: 450px)";

    constructor(props) {
        super(props)
        this.state = { matches: window.matchMedia(this.mediaQuery).matches };
    }

    handleEmailChange(e) {
        this.setState({email: e.target.value});
    }

    handlePassword1Change(e) {
        this.setState({password1: e.target.value});
    }

    handlePassword2Change(e) {
        this.setState({password2: e.target.value});
    }

    componentDidMount() {
        const handler = e => this.setState({matches: e.matches});
        window.matchMedia(this.mediaQuery).addListener(handler);
    }

    registerSubmit(e) {
        e.preventDefault();

    }

    render() {
        let output = <div className= {this.state.matches? "container w-100 mt-5" : "container w-50 mt-5" }>
            <form onSubmit= {this.registerSubmit.bind(this)}>
                <div className="form-group">
                    <input type="email" className="form-control" id="emailInput" aria-describedby="emailHelp" onChange={this.handleEmailChange} placeholder="E-mail"/>
                </div>
                <div className="form-group">
                    <input type="password" className="form-control" id="passInput1" onChange={this.handlePassword1Change} placeholder="Lozinka"/>
                </div>
                <div className="form-group">
                    <input type="password" className="form-control" id="passInput2" onChange={this.handlePassword2Change} placeholder="Potvrdi lozinku"/>
                </div>
                <button type="submit" className="btn btn-primary">Registruj se</button>
            </form>
        </div>

        return output;
    }
}

export default Register;
