import React, { Component } from 'react';
import ItemCard from './ItemCard';
import axios from 'axios';


class ItemList extends Component {
    state = { items: [] }

    componentDidMount() {
        this.fetchItems();
        console.log('this.props', this.props);
    }

    componentDidUpdate(prevProps) {
        if(this.props.location !== prevProps.location) {
            this.fetchItems();
        }
    }

    fetchItems = () => {
        Axios.get(`http://www.omdbapi.com/?apikey=aa9e49f&s`)
            .then(resp=>resp.data)
            .then(data=>data.Search)
            .then(item=>this.setState({item}))

        axios({
            method: "post",
            url: "http://localhost:8000/api/user/create/",
            data: bodyFormData,
            headers: {'Content-Type': 'multipart/form-data' }
            })
            .then(response => {
                alert('Uspešna registracija, možete se ulogovati')
                this.props.history.push('/login');
            })
            .catch(err => {
                alert('Neuspešna akcija, pokušajte ponovo')
            });

    }
    render() {
        const itemList = this.state.items.map((item, index) => <ItemCard item={item} key={index} />)
        return (
            <div className="row">
                {itemList}
            </div>
        );
    }
}

export default ItemList;
